import {configureStore} from '@reduxjs/toolkit';
import rootReducer from '../reducer.js';
import { sayHiOnDispatch,includeMeaningOfLife } from '../exampleAddons/enhancers.js';

let preloadedState;
const persistedTodoString=localStorage.getItem('todos');

if(persistedTodoString){
    preloadedState={
        todos:JSON.parse(persistedTodoString)
}
}

const store=configureStore({
    reducer:rootReducer,
    enhancers:sayHiOnDispatch
})

export default store;
